import {writeFile} from 'node:fs/promises';
import {getDirectories} from './get-directories.helper.ts';

const data = `/**
 * This file is automatically generated by \`./script/build-synthetic-index.mjs\`, it should not be modified manually or added to git.
 */
 
${getDirectories('./src')
	.map(dir => `export * from './${dir}';`)
	.join('\n')}
`;

await writeFile('./src/index.ts', data);